let authToken = null

console.log('[TEST] Starting script execution')

// Test 1: Login and get token
hopp.test('Login test', async () => {
  console.log('[LOGIN] Test callback started')
  const response = await hopp.fetch('https://echo.hoppscotch.io?action=login')
  console.log('[LOGIN] Fetch completed')
  const data = await response.json()
  console.log('[LOGIN] JSON parsed')
  authToken = 'token-' + Date.now()
  console.log('[LOGIN] Token set to:', authToken)
  hopp.expect(response.status).toBe(200)
  console.log('[LOGIN] Test callback completed')
})

// Test 2: Use token from Test 1
hopp.test('Authenticated request', async () => {
  console.log('[AUTH] Test callback started')
  console.log('[AUTH] Using token:', authToken)

  if (authToken === null) {
    console.log('[AUTH] ERROR: Token is null! Login test has not completed yet!')
  } else {
    console.log('[AUTH] SUCCESS: Token is available')
  }

  const response = await hopp.fetch('https://echo.hoppscotch.io?token=' + authToken)
  console.log('[AUTH] Fetch completed')
  const data = await response.json()
  console.log('[AUTH] Response args:', JSON.stringify(data.args))

  // This should pass if login completed first
  hopp.expect(authToken).not.toBe(null)
  hopp.expect(data.args.token).toBe(authToken)
  console.log('[AUTH] Test callback completed')
})

console.log('[TEST] Script execution completed')
