requests.preRequestScript:
console.log('[TEST] Starting Headers test')

try {
  console.log('[TEST] Creating Headers object')
  const headers = new Headers({
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  })
  console.log('[TEST] Headers created successfully')

  console.log('[TEST] Testing keys() method')
  const keys = headers.keys()
  console.log('[TEST] keys() returned:', typeof keys)
  console.log('[TEST] keys is array?', Array.isArray(keys))

  console.log('[TEST] Attempting for...of iteration')
  let keyCount = 0
  for (const key of keys) {
    console.log('[TEST] Got key:', key)
    keyCount++
  }
  console.log('[TEST] Total keys:', keyCount)

  hopp.env.active.set('iteration_success', 'true')
  hopp.env.active.set('key_count', keyCount.toString())
} catch (error) {
  console.error('[TEST] Error:', error.message)
  hopp.env.active.set('iteration_success', 'false')
  hopp.env.active.set('error_msg', error.message)
}

requests.testScript:
hopp.test('Headers iteration worked', () => {
  const success = hopp.env.active.get('iteration_success')
  console.log('Iteration success:', success)
  if (success !== 'true') {
    const errorMsg = hopp.env.active.get('error_msg')
    console.error('Error message:', errorMsg)
  }
  hopp.expect(success).toBe('true')
})
