requests.preRequestScript:
export {};
console.log('Creating FormData...')
const formData = new FormData()
formData.append('field1', 'value1')
formData.append('field2', 'value2')
console.log('FormData created')

try {
  console.log('Calling fetch...')
  const response = await hopp.fetch('https://echo.hoppscotch.io', {
    method: 'POST',
    body: formData
  })
  console.log('Fetch returned, status:', response.status)

  const data = await response.json()
  console.log('Data:', JSON.stringify(data, null, 2))

  hopp.env.active.set('formdata_status', response.status.toString())
  hopp.env.active.set('formdata_method', data.method)
} catch (error) {
  console.error('Error:', error)
  hopp.env.active.set('formdata_status', 'error')
  hopp.env.active.set('formdata_error', String(error))
}

requests.testScript:
export {};
hopp.test('FormData POST works', () => {
  const status = hopp.env.active.get('formdata_status')
  console.log('Test - status:', status)
  hopp.expect(status).toBe('200')
})
