requests.preRequestScript:
export {};
console.log('Fetching...')
const response = await hopp.fetch('https://echo.hoppscotch.io')
console.log('Response received, status:', response.status)
console.log('response.text type:', typeof response.text)

try {
  console.log('Calling response.text()...')
  const text = await response.text()
  console.log('text() returned, type:', typeof text, 'length:', text.length)
  hopp.env.active.set('text_result', 'success')
  hopp.env.active.set('text_length', String(text.length))
} catch (error) {
  console.error('Error calling text():', error)
  console.error('Error type:', typeof error)
  console.error('Error message:', error.message || String(error))
  hopp.env.active.set('text_result', 'error: ' + (error.message || String(error)))
}

requests.testScript:
export {};
hopp.test('text() method works', () => {
  const result = hopp.env.active.get('text_result')
  console.log('Test result:', result)
  hopp.expect(result).toBe('success')
})
